function Medida(e,t){expr=XRegExp("(?<num>      [-+]?[^\\.][0-9]+([\\.][0-9]+)?\\s*(?:e[+-]?[ ]*[0-9]+)?)\\s*   # numero       \n(?<temp1>    [fkcFKC])\\s*                                                   # temperatura1","x"),t?(this.value=e,this.type=t):(cadena=XRegExp.exec(e,expr),this.value=cadena.num,this.type=cadena.temp1)}Medida.match=function(e){return expresion=XRegExp("(?<num>   [-+]?[^\\.][0-9]+([\\.][0-9]+)?\\s*(?:e[+-]?[ ]*[0-9]+)?)\\s*   # numero       \n(?<temp1>    [fkcFKC])\\s*                                       # temperatura1 \n(?<to>       (?:to)?)\\s*                                        # to           \n(?<temp2>    [fkcFKC])\\s*                                       # temperatura2","x"),e=XRegExp.exec(e,expresion)},Medida.measures={},Medida.convertir=function(e){var t=Medida.measures;t.c=Celsius,t.f=Farenheit,t.k=Kelvin;var a=Medida.match(e);if(!a)return"Introduzca una temperatura valida: 32.6e-8 F to C";var r=parseFloat(a.num),n=a.temp1.toLowerCase(),o=a.temp2.toLowerCase();try{console.log(t[n]);var s=new t[n](r),c="to"+t[o].name;return s[c]().toFixed(2)+" "+t[o].name}catch(u){return console.log(u),'Desconozco como convertir desde "'+n+'" hasta "'+o+'"'}};